import merge from 'lodash/merge.js';
export const rules = {
    '.uk-marker': {
        padding: '5px',
        backgroundColor: '#222',
        color: '#fff',
        borderRadius: '500px'
    },
    '.uk-marker:hover': { color: '#fff' }
};
export const addHooks = (args) => {
    const { hooks } = args;
    const defaultHooks = {
        'hook-marker': {},
        'hook-hover': {},
        'hook-misc': {}
    };
    const _hooks = merge(defaultHooks, hooks);
    return {
        '.uk-marker': {
            ..._hooks['hook-marker']
        },
        '.uk-marker:hover': {
            ..._hooks['hook-hover']
        },
        ..._hooks['hook-misc']
    };
};
