import merge from 'lodash/merge.js';
export const rules = {
    '.uk-totop': {
        padding: '5px',
        color: '#999',
        transition: 'color 0.1s ease-in-out'
    },
    '.uk-totop:hover': { color: '#666' },
    '.uk-totop:active': { color: '#333' }
};
export const addHooks = (args) => {
    const { hooks } = args;
    const defaultHooks = {
        'hook-totop': {},
        'hook-hover': {},
        'hook-active': {},
        'hook-misc': {}
    };
    const _hooks = merge(defaultHooks, hooks);
    return {
        '.uk-totop': {
            ..._hooks['hook-totop']
        },
        '.uk-totop:hover': {
            ..._hooks['hook-hover']
        },
        '.uk-totop:active': {
            ..._hooks['hook-active']
        },
        ..._hooks['hook-misc']
    };
};
