func getCategoryColor(category string) string {
	switch category {
	case "bug":
		return "danger"
	case "feature":
		return "primary"
	case "improvement":
		return "success"
	case "question":
		return "warning"
	default:
		return "default"
	}
}

func getPriorityColor(priority string) string {
	switch priority {
	case "critical":
		return "danger"
	case "high":
		return "warning"
	case "medium":
		return "primary"
	case "low":
		return "secondary"
	default:
		return "default"
	}
}

func getStatusColor(status string) string {
	switch status {
	case "open":
		return "success"
	case "in_progress":
		return "primary"
	case "closed":
		return "secondary"
	default:
		return "default"
	}
}

